pragma solidity ^0.8.21;

import {
    RMRKAbstractNestableMultiAsset
} from "../abstract/RMRKAbstractNestableMultiAsset.sol";
import {RMRKImplementationBase} from "../utils/RMRKImplementationBase.sol";
import {RMRKTokenURIPerToken} from "../utils/RMRKTokenURIPerToken.sol";

/**
 * @title RMRKNestableMultiAssetPreMint
 * @author RMRK team
 * @notice Implementation of joined RMRK nestable and multi asset modules with pre-minting.
 */
contract RMRKNestableMultiAssetPreMint is
    RMRKTokenURIPerToken,
    RMRKAbstractNestableMultiAsset
{
    /**
     * @notice Used to initialize the smart contract.
     * @param name Name of the token collection
     * @param symbol Symbol of the token collection
     * @param collectionMetadata CID of the collection metadata
     * @param maxSupply The maximum supply of tokens
     * @param royaltyRecipient Recipient of resale royalties
     * @param royaltyPercentageBps The percentage to be paid from the sale of the token expressed in basis points
     */
    constructor(
        string memory name,
        string memory symbol,
        string memory collectionMetadata,
        uint256 maxSupply,
        address royaltyRecipient,
        uint16 royaltyPercentageBps
    )
        RMRKImplementationBase(
            name,
            symbol,
            collectionMetadata,
            maxSupply,
            royaltyRecipient,
            royaltyPercentageBps
        )
    {}

    /**
     * @notice Used to mint the desired number of tokens to the specified address.
     * @dev The `data` value of the `_safeMint` method is set to an empty value.
     * @dev Can only be called while the open sale is open.
     * @param to Address to which to mint the token
     * @param numToMint Number of tokens to mint
     * @param tokenURI URI assigned to all the minted tokens
     * @return firstTokenId The ID of the first token to be minted in the current minting cycle
     */
    function mint(
        address to,
        uint256 numToMint,
        string memory tokenURI
    ) public virtual onlyOwnerOrContributor returns (uint256 firstTokenId) {
        (uint256 nextToken, uint256 totalSupplyOffset) = _prepareMint(
            numToMint
        );

        for (uint256 i = nextToken; i < totalSupplyOffset; ) {
            _setTokenURI(i, tokenURI);
            _safeMint(to, i, "");
            unchecked {
                ++i;
            }
        }

        firstTokenId = nextToken;
    }

    /**
     * @notice Used to mint a desired number of child tokens to a given parent token.
     * @dev The `data` value of the `_safeMint` method is set to an empty value.
     * @dev Can only be called while the open sale is open.
     * @param to Address of the collection smart contract of the token into which to mint the child token
     * @param numToMint Number of tokens to mint
     * @param destinationId ID of the token into which to mint the new child token
     * @param tokenURI URI assigned to all the minted tokens
     * @return firstTokenId The ID of the first token to be minted in the current minting cycle
     */
    function nestMint(
        address to,
        uint256 numToMint,
        uint256 destinationId,
        string memory tokenURI
    ) public virtual onlyOwnerOrContributor returns (uint256 firstTokenId) {
        (uint256 nextToken, uint256 totalSupplyOffset) = _prepareMint(
            numToMint
        );

        for (uint256 i = nextToken; i < totalSupplyOffset; ) {
            _setTokenURI(i, tokenURI);
            _nestMint(to, i, destinationId, "");
            unchecked {
                ++i;
            }
        }

        firstTokenId = nextToken;
    }
}

